import react ,{useState} from "react";
import "./LoginForm.css";


const LoginForm = () => {

    const [NameItem , setNameItem] = useState(" ");
    const [SurnameItem , setSurnameItem] = useState(" ");
    const [Items,SetItems]=useState([]);

    if(NameItem && !SurnameItem){
        alert("Ad ve Soyad Alanı Boş Bırakılamaz")
        return;
    }

    const AddItem = () => {
        const item = {
            id:Math.floor(Math.random()*1000),
            value: NameItem + SurnameItem
        }

        console.log(item)
        console.log(Items)
        SetItems(oldItems => [...oldItems,item])
        setNameItem(" ");
        setSurnameItem(" ");

    }

    return (
        <div  id="LoginBox" >
            <div className="row w-50 mx-auto pt-5">
                <div className="col-12 p-0">
                    <label htmlFor="">İsim</label>
                    <input className="form-control" value={NameItem} onChange={e => setNameItem(e.target.value)} type="text"/>

                    <label htmlFor="">Soyad</label>
                    <input value={SurnameItem} onChange={e => setSurnameItem(e.target.value)} className="form-control my-2" type="text"/>
                </div>
                <div className="col-12 p-0">
                    <label htmlFor="">Email</label>
                    <input className="form-control" type="email" />

                    <label htmlFor="">Doğum Tarihi</label>
                    <input className="form-control my-2" type="date" name="" id="" />
                </div>
                <div className="row justify-content-center pt-2 ">
                    <button onClick={()=>AddItem()} className="btn btn-outline-primary w-25">Kaydet</button>
                </div>
            </div>

            <div className="row pt-4 w-50 mx-auto">
                <h2 className="display-6 text-center">Liste</h2>

                <ul className="list-group-numbered">
                    {Items.map(item => {
                        return (
                              <li key={item.id} className="list-group-item">
                            {item.value}
                        </li>
                        )
                    })}
                </ul>
            </div>
        </div>
    )
}

export default LoginForm
